INSERT INTO `tbl_feedback` (`feedback_id`, `ho_ten_khach`, `anh_khach`, `noi_dung`) VALUES
(1, 'Lương Thị Thùy Dung', 'https://cafebiz.cafebizcdn.vn/zoom/600_315/162123310254002176/2023/8/2/avatar1690992247147-16909922474271475139585.png', 'Shop Phoneplus có một sự đa dạng rất lớn về các mẫu điện thoại từ các thương hiệu khác nhau. Tôi rất hài lòng vì có thể tìm thấy nhiều lựa chọn phù hợp với nhu cầu và sở thích của mình. Sản phẩm điện thoại tại shop Phoneplus đều được đảm bảo chất lượng. Tôi đã mua một chiếc điện thoại từ đó và cho đến nay nó vẫn hoạt động tốt mà không gặp phải bất kỳ vấn đề nào. Điều này chứng tỏ shop Phoneplus cung cấp những sản phẩm chất lượng và tin cậy.'),
(2, 'Lưu Quang Hà ', 'https://toplist.vn/images/800px/oppo-1064041.jpg', 'Tôi rất hài lòng với trải nghiệm mua hàng điện thoại tại shop Phoneplus. Sự đa dạng sản phẩm, chất lượng, dịch vụ khách hàng tận tâm, giá cả hợp lý và dịch vụ sau bán hàng tốt là những điểm mạnh của shop này. Tôi sẽ khuyên bạn nếu bạn đang tìm kiếm một nơi đáng tin cậy để mua điện thoại di động.\r\n'),
(3, 'Bùi Tú Phương ', 'https://image-us.24h.com.vn/upload/2-2023/images/2023-06-03/1685789967-821-bai-tet-4-iphone-chinh-hang-dang-mua-nhat-dau-nam-2023-1-1673513411-767-width1600height1000-1685789967-width1600height1000.jpg', 'Shop Phoneplus là một địa điểm mua hàng điện thoại tuyệt vời. Tôi đã có trải nghiệm tuyệt đối với cửa hàng này. Họ cung cấp một sự đa dạng sản phẩm rất lớn từ các thương hiệu uy tín. Chất lượng sản phẩm rất tốt và đội ngũ nhân viên thân thiện và nhiệt tình. Giá cả cạnh tranh và dịch vụ sau bán hàng tốt. Tôi rất hài lòng với trải nghiệm mua hàng tại shop Phoneplus và tôi sẽ khuyên bạn nên ghé thăm cửa hàng này nếu bạn đang tìm kiếm một chiếc điện thoại mới.'),
(4, 'Nguyễn Trung Kiên ', 'https://cafefcdn.com/thumb_w/640/203337114487263232/2023/5/14/avatar1684027374624-1684027375208495162627-0-83-628-1088-crop-16840274151351664674566.jpg', 'Phoneplus là một điểm đến tuyệt vời để mua điện thoại. Tôi đã có trải nghiệm tuyệt hảo với cửa hàng này. Họ không chỉ có một sự lựa chọn đa dạng về các sản phẩm từ các thương hiệu hàng đầu, mà còn cung cấp chất lượng sản phẩm xuất sắc. Đội ngũ nhân viên thân thiện và am hiểu về sản phẩm, luôn sẵn lòng tư vấn và giúp đỡ. Giá cả cạnh tranh và dịch vụ sau bán hàng chất lượng, họ luôn đảm bảo rằng khách hàng hài lòng và được hỗ trợ sau khi mua hàng. Tôi rất ấn tượng với shop Phoneplus và sẽ tiếp tục ủng hộ họ trong tương lai. Nếu bạn đang muốn mua điện thoại, tôi chắc chắn khuyên bạn nên ghé qua shop Phoneplus.');

